mapper.select:
SELECT DISTINCT p.*, u.username as author_username, u.nickname as author_nickname, u.avatar as author_avatar, c.name as category_name, t.id as tag_id, t.name as tag_name FROM post p LEFT JOIN user u ON p.author_id = u.id LEFT JOIN category c ON p.category_id = c.id LEFT JOIN post_tag pt ON p.id = pt.post_id LEFT JOIN tag t ON pt.tag_id = t.id WHERE p.status = #{status} AND p.is_deleted = 0
AND (p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%'))
ORDER BY p.is_top DESC, p.create_time DESC LIMIT #{offset}, #{size}

SELECT DISTINCT p.*, u.username as author_username, u.nickname as author_nickname, u.avatar as author_avatar, c.name as category_name, t.id as tag_id, t.name as tag_name FROM post p LEFT JOIN user u ON p.author_id = u.id LEFT JOIN category c ON p.category_id = c.id LEFT JOIN post_tag pt ON p.id = pt.post_id LEFT JOIN tag t ON pt.tag_id = t.id
p.title LIKE CONCAT('%', #{keyword}, '%') OR p.content LIKE CONCAT('%', #{keyword}, '%')
ORDER BY p.create_time DESC LIMIT #{offset}, #{size}

mapper.update:
UPDATE post
title = #{title}, summary = #{summary}, content = #{content}, cover_image = #{coverImage}, category_id = #{categoryId}, status = #{status},
WHERE id = #{id}
